import React, { useState } from 'react';

const ManageUsers = () => {
    const [userId, setUserId] = useState("");

    const handleDelete = async () => {
        
    };

    const handleBan = async () =>{


        const response = await fetch('http://localhost:8000/',{  
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({
                user: userId
                
            }),
            
        });
    
        if (!response.ok) {
            // handle error
            console.log('post request failed from front end' + response.status);
            // toast.error(responseData.error)
        } else {
            // handle success
            console.log('post request succesfully sent from front end' + response.status);
        }

    }

    

    return (
        
        <div className='register--section '>
            
            <h2>Manage Users</h2>
            <p className='text-lg'>Please enter the id of the user you would like to ban / remove!</p>


            
            <form register--form--container>
                <div className='container'>
                    <label className='register--label'>
                         <input className='register--input text-md'
                          type="text"
                         placeholder="Enter User ID"
                         value={userId}
                         onChange={(e) => setUserId(e.target.value)}
                         required
                />

                    </label>
                

                </div>
                <div>
                <button type="submit" className='btn btn-primary'  onClick={()=> handleDelete()}>Remove User</button>
                <button type="submit" className='btn btn-primary'  onClick={()=> handleBan()}>Ban User</button>

                </div>
               
            </form>
        </div>
    );
};

export default ManageUsers;
